/*Table structure for table `tbl_bucketproduct` */

DROP TABLE IF EXISTS `tbl_bucketproduct`;

CREATE TABLE `tbl_bucketproduct` (
  `brandproductid` int(11) NOT NULL AUTO_INCREMENT,
  `groupid` int(11) NOT NULL,
  `subgroup_id` int(11) NOT NULL,
  `brandbucketid` int(11) NOT NULL,
  `desc` varchar(50) DEFAULT NULL,
  `iconimage` longblob,
  `createdby` int(11) NOT NULL,
  `createdon` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `updatedby` int(11) DEFAULT NULL,
  `updatedon` timestamp NULL DEFAULT NULL,
  `isactive` tinyint(1) NOT NULL DEFAULT '1',
  PRIMARY KEY (`brandproductid`),
  KEY `FK_Brandbucket` (`brandbucketid`),
  KEY `FK_Group` (`groupid`),
  KEY `FK_Subgroup` (`subgroup_id`),
  CONSTRAINT `FK_Brandbucket` FOREIGN KEY (`brandbucketid`) REFERENCES `tbl_brandbucket` (`brandbucketid`) ON UPDATE CASCADE,
  CONSTRAINT `FK_Group` FOREIGN KEY (`groupid`) REFERENCES `tbl_bucketgroup` (`group_id`) ON UPDATE CASCADE,
  CONSTRAINT `FK_Subgroup` FOREIGN KEY (`subgroup_id`) REFERENCES `tbl_bucketsubgroup` (`subgroup_id`) ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

