/*Table structure for table `tbl_bucketsubgroup` */

DROP TABLE IF EXISTS `tbl_bucketsubgroup`;

CREATE TABLE `tbl_bucketsubgroup` (
  `subgroup_id` int(11) NOT NULL AUTO_INCREMENT,
  `group_id` int(11) NOT NULL,
  `brandbucketid` int(11) NOT NULL,
  `desc` varchar(50) DEFAULT NULL,
  `iconimage` longblob,
  `createdby` int(11) NOT NULL,
  `createdon` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `updatedby` int(11) DEFAULT '0',
  `updatedon` timestamp NULL DEFAULT NULL,
  `isactive` int(11) NOT NULL DEFAULT '1',
  PRIMARY KEY (`subgroup_id`),
  KEY `FK_GroupId` (`group_id`),
  KEY `BrandBucket` (`brandbucketid`),
  CONSTRAINT `BrandBucket` FOREIGN KEY (`brandbucketid`) REFERENCES `tbl_brandbucket` (`brandbucketid`) ON UPDATE CASCADE,
